@import url('https://fonts.googleapis.com/css2?family=Pacifico&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Pacifico&family=Varela+Round&display=swap');

nav{
    font-family: "Varela Round", sans-serif;
  font-weight: 600;
  font-style: normal;
    color: aliceblue;
    display: flex;
    align-items: center;
    justify-content: space-between;
}
.small-menu{
    display: none;
}


.name{
    font-family: "Pacifico", cursive;
  font-weight: 400;
  font-style: normal;
  font-size: 2rem;
  margin-right: 20px;
    margin-left: 20px;
}

.link-container{
    list-style: none;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 30px;
    
}
.link-lists a{
    text-decoration: none;
    color: aliceblue;
    font-family: "Varela Round", sans-serif;
  font-weight: 600;
  font-style: normal;
  font-size: 1.2rem;
  position: relative;
  padding: 10px;
  
}

.link-lists a::after{
    content: "";
    height: 2px;
    width: 0;
    background-color: #646cff;
    position: absolute;
    transition: all .3s ease;
    bottom: 0;
    left: 10px;
    

}
.link-lists a:hover::after{
    width: 70%;
    color: black;
}

.contact-me{
    color: aliceblue;
    font-family: "Varela Round", sans-serif;
  font-weight: 600;
  font-style: normal;
    padding: 10px;
    margin-right: 20px;
    margin-left: 20px;
        border-radius: 8px;
        border: 1px solid transparent;
        padding: 0.6em 1.2em;
        font-size: 1em;
        
        background-color: #1a1a1a;
        cursor: pointer;
        transition: border-color 0.25s;

}
button:hover {
    border-color: #646cff;
  }
  button:focus,
  button:focus-visible {
    outline: 4px auto -webkit-focus-ring-color;
  }
  
  .menu-icon{
    display: none;
    position: absolute;
  }
  .menu{
    position: absolute;
  }


  @media (max-width: 1024px) {
    .name{
        font-size: 1.5rem;
    }
    .link-lists a {
        font-size: 1rem;
    }
    .contact-me {
        font-size: .8rem;
    }
  }

  @media (max-width: 768px) {
    .name{
        font-size: 1.3rem;
    }
    .link-container{
        display: none;
    }
    .contact-me{
        display: none;
    }
    nav{
        justify-content: unset;
        align-items: unset;
    }
    .menu-icon{
        display: contents;
        
    }
    .mini-nav{
        background-color: rgba(240, 248, 255, 0.815);
        width: 100%;
        text-align: center;
        font-size: 1rem;
        list-style: none;
        line-height: 40px;
        position: absolute;
        right: 0;
        top: 50px;

    }


    .mini-nav-links a {
        text-decoration: none;
        color: #1a1a1a;
        
    }

    .menu{
        position: absolute;
        right: 0;
        margin-top: 25px;
        margin-right: 20px;
    }
    .open{
        display: contents;
    }

  }

