.home-page{
    height: 100vh;
    border: 1px solid red;
    width: 100%;
}
.container{
    width: 90%;
    border: 1px solid red;
    height: 80%;
    margin: auto;
    display: flex;
}
.side-line{
    border: 1px solid red;
    width: 8.3%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    position: absolute;
}
.line-1{
    height: 40%;
    width: 4%;
    margin-top: 10%;
    background: linear-gradient(transparent, #7C72FF 30%);

}
.middle-icon{
    margin: 20px;
    width: 24px;
    
    
}
.octicon{
    box-shadow: #313bf2;
    width: 100%;
}
.octicon:hover{
    transform: scale(110%);
    
}

.line-2{
    height: 60%;
    width: 4%;
    background: linear-gradient(#7C72FF, #2DA44E 80%, #3FB950);
    

}







.about-container{
    /* border: 1px solid blue; */
    width: 100%;
    padding-left: 20px;
    padding-top: 40px;
    font-family: "Mona Sans", "Mona Sans Header Fallback", -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    margin-left: 10%;
}
.about__1{
    font-size: 3rem;
    color: aliceblue;
    
}
.about__heading{
    
    font-size: 5rem;
    color: aliceblue;
    position: relative;
    top: -60px;
    
}

.about__desc{
    font-weight: 400;
    position: relative;
    top: -120px;
    color: rgb(164, 164, 164);
}

.about__btn{
    font-weight: 600;
    font-style: normal;
          border-radius: 8px;
          border: 1px solid transparent;
          padding: 0.6em 1.2em;
          font-size: 1em;
          
          background-color: #1a1a1a;
          border-color: #7C72FF;
          cursor: pointer;
          transition: border-color 0.25s;
          color: aliceblue;
          position: relative;
          top: -100px;

}


@media (max-width: 1440px) {
    .about__1{
        font-size: 2.75rem;
    }
    .about__heading{
        font-size: 4.5rem;
        top: -60px;
    }
    .about__btn{
        top: -110px;
        padding: 0.6em 1.2em;
          font-size: .9rem;
    }
}
@media (max-width: 1020px) {
    .about__1{
        font-size: 2.4rem;
    }
    .about__heading{
        font-size: 4rem;
        top: -40px;
    }
    .about__desc{
        font-size: 1rem;
        top: -80px;
    }
    .about__btn{
        top: -75px;
        padding: 0.6em 1.2em;
          font-size: .8rem;
    }
}

@media (max-width: 768px) {
    .about__1{
        font-size: 2.2rem;
    }
    .about__heading{
        font-size: 3.5rem;
        top: -40px;
    }
    .about__desc{
        font-size: 1rem;
        top: -80px;
    }
    .about__btn{
        top: -75px;
        padding: 0.6em 1.2em;
          font-size: .8rem;
    }
}
@media (max-width: 425px) {
    .about__1{
        font-size: 2rem;
    }
    .about__heading{
        font-size: 3rem;
        top: -40px;
    }
    .about__desc{
        font-size: 1rem;
        top: -70px;
    }
    .about__btn{
        top: -65px;
        padding: 0.6em 1.2em;
          font-size: .65rem;
    }
}

